package com.corejava.operators;

public class Assignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * assignment operators
		  =         a=15        a=15
		  +=        a+=15       a=a+15 
		  -+        a-=15       a=a-15 
		  *=        a*=15       a=a*15 
		  /=        a/=15       a=a/15 
		  %=        a%=15       a=a%15 
		 */    
				short a=100;
				System.out.println(a);
				a+=15;
				System.out.println(a);
				a-=15;
				System.out.println(a);
				a*=15;
				System.out.println(a);
				a/=15;
				System.out.println(a);
				a%=15;
				System.out.println(a);		
    }
}
